mapper.select:
select seq, name, annual, vacation, start_date, end_date from (
select row_number() over(order by seq desc) AS rno , seq , name, annual, vacation , to_char(start_date, 'yyyy-mm-dd') as start_date , to_char(end_date, 'yyyy-mm-dd') as end_date from tbl_vacation where name = '이순신3'
) V where rno between #{startRno} and #{endRno}

mapper.update:
		update tbl_vacation set annual = #{annual}
                     , vacation = #{vacation}
                     , start_date = #{start_date}
                     , end_date = #{end_date}
      	where seq = #{seq}
